import AppProductList from './components/ProductList.vue';
import products from './data/products';
import AppBasePagination from './components/BasePagination.vue';
import AppProductFilter from './components/ProductFilter.vue';
export default {
  name: 'App',
  components: {
    AppProductList,
    AppBasePagination,
    AppProductFilter
  },

  data() {
    return {
      filterPriceFrom: 0,
      filterPriceTo: 0,
      filterCategoryId: 0,
      startColor: 0,
      page: 1,
      productsPerPage: 6
    };
  },

  computed: {
    filteredProducts() {
      let filteredProducts = products;

      if (this.filterPriceFrom > 0) {
        filteredProducts = filteredProducts.filter(product => product.price > this.filterPriceFrom);
      }

      if (this.filterPriceTo > 0) {
        filteredProducts = filteredProducts.filter(product => product.price < this.filterPriceTo);
      }

      if (this.filterCategoryId > 0) {
        filteredProducts = filteredProducts.filter(product => product.categoryId === this.filterCategoryId);
      }

      if (this.startColor && this.startColor.length) {
        filteredProducts = filteredProducts.filter(product => product.colors.indexOf(this.startColor));
      }

      return filteredProducts;
    },

    products() {
      const offset = (this.page - 1) * this.productsPerPage;
      return this.filteredProducts.slice(offset, offset + this.productsPerPage);
    },

    countProducts() {
      return this.filteredProducts.length;
    }

  }
};
