import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    cartProducts: [{productId: 1, amount: 2}],
  },
  mutations: {
    addProductToCart(state, {productId, amount}) {
      const item = state.cartProducts.

      state.cartProducts.push({
        productId,
        amount,
      })
    },
  },
})
